import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Product } from '../Models/Product';
import { ProductService } from '../services/product.service';

@Component({
  selector: 'app-product-details',
  templateUrl: './product-details.component.html',
  styleUrls: ['./product-details.component.css']
})
export class ProductDetailsComponent implements OnInit {
id!:number;
param2!:number;
P:Product={id:1, title:"test", price:10, quantity:0, like:0};

constructor(private actR:ActivatedRoute, private productS: ProductService){}

ngOnInit(): void {
  this.id=this.actR.snapshot.params['id1'];

  //this.productS.getProductById(this.id).subscribe(data => this.P = data);

}

updateProduct(){}

}
<h1>Congrats! Id: {{id}}  </h1>


<h2 class="center">Update Product</h2>

<form  #addForm="ngForm"  (ngSubmit)="updateProduct()">
    <div class="container">
    <div class="mb-3 ">
      <label  class="form-label">Title</label>
      <input type="text" class="form-control" name="title" [(ngModel)]="P.title"  required  minlength="4"  >

    </div>

    <div class="mb-3">
      <label  class="form-label">Price</label>
      <input type="text" class="form-control"  name="price"  [(ngModel)]="P.price" >
    </div>

    <div class="mb-3">
        <label  class="form-label">Quantity</label>
        <input type="number" class="form-control"  name="qantity"  [(ngModel)]="P.quantity" >
      </div>
    <button type="submit" class="btn btn-primary"  [disabled]="addForm.invalid">Submit</button>
</div>
  </form>
